CREATE TABLE `admin` (
  `id` INTEGER(11) NOT NULL AUTO_INCREMENT,
  `email` VARCHAR(200) COLLATE utf8_general_ci NOT NULL,
  `password` VARCHAR(255) COLLATE utf8_general_ci NOT NULL,
  PRIMARY KEY USING BTREE (`id`)
) ENGINE=InnoDB
AUTO_INCREMENT=2 CHARACTER SET 'latin1' COLLATE 'latin1_swedish_ci'
;

/* Structure for the `users` table : */

CREATE TABLE `users` (
  `id` INTEGER(11) NOT NULL AUTO_INCREMENT,
  `username` VARCHAR(160) COLLATE utf8_general_ci NOT NULL,
  `surname` VARCHAR(160) COLLATE utf8_general_ci NOT NULL DEFAULT '',
  `password` VARCHAR(160) COLLATE utf8_general_ci NOT NULL,
  `email` VARCHAR(160) COLLATE utf8_general_ci NOT NULL,
  `lat` VARCHAR(60) COLLATE utf8_general_ci NOT NULL,
  `lng` VARCHAR(60) COLLATE utf8_general_ci NOT NULL,
  `createdDate` DATE NOT NULL,
  PRIMARY KEY USING BTREE (`id`)
) ENGINE=InnoDB
AUTO_INCREMENT=13 CHARACTER SET 'latin1' COLLATE 'latin1_swedish_ci'
;

/* Structure for the `videos` table : */

CREATE TABLE `videos` (
  `id` INTEGER(11) NOT NULL AUTO_INCREMENT,
  `userId` INTEGER(11) NOT NULL,
  `videos` VARCHAR(500) COLLATE utf8_general_ci NOT NULL DEFAULT '',
  `videoPath` LONGTEXT COLLATE utf8_general_ci NOT NULL,
  `videoName` VARCHAR(160) COLLATE utf8_general_ci NOT NULL,
  `uploadedDate` DATETIME NOT NULL,
  `lat` VARCHAR(30) COLLATE utf8_general_ci NOT NULL,
  `lng` VARCHAR(30) COLLATE utf8_general_ci NOT NULL,
  `state` TINYINT(4) NOT NULL DEFAULT 0,
  PRIMARY KEY USING BTREE (`id`)
) ENGINE=InnoDB
AUTO_INCREMENT=41 CHARACTER SET 'latin1' COLLATE 'latin1_swedish_ci'
;

